package edu.tum.cs.i1.pse;public class Transpose implements CipherBase {	public String encryptWord(String w, Object k) {		return reverse(w);	}	public String decryptWord(String w, Object k) {		return reverse(w);	}	private String reverse(String w) {		if (w.contains(" ")) {			throw new IllegalArgumentException("No spaces allowed!");		}		String result = "";		for (int i = w.length() - 1; i >= 0; i--)			result = result + w.charAt(i);		return result;	}}